    <?php
    if ($_SERVER['REQUEST_METHOD'] === 'POST') {
        $producto = $_POST['producto'];
        $cantidad = (int)$_POST['cantidad'];
        $precio = (float)$_POST['precio'];
        $totalAntesDescuento = $cantidad * $precio;
        if ($cantidad >= 5 && $cantidad <= 10) {
            $descuento = 0.1;
        } elseif ($cantidad > 10) {
            $descuento = 0.2;
        } else {
            $descuento = 0;
        }
        $totalDespuesDescuento = $totalAntesDescuento - ($totalAntesDescuento * $descuento);
        echo "<h2>Factura de la compra:</h2>";
        echo "<p><strong>Producto:</strong> " . $producto . "</p>";
        echo "<p><strong>Cantidad:</strong> " . $cantidad . "</p>";
        echo "<p><strong>Precio unitario:</strong> $" . $precio . "</p>";
        echo "<p><strong>Total antes del descuento:</strong> $" . $totalAntesDescuento . "</p>";
        echo "<p><strong>Descuento aplicado:</strong> " . ($descuento * 100) . "%</p>";
        echo "<p><strong>Total con descuento:</strong> $" . number_format($totalDespuesDescuento, 2, '.', ',') . "</p>";
    }

    ?>
</body>
